// src/Question.js
import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { marked } from 'marked';
import './Question.css'; // Ensure you have appropriate styles

function Question({ question, options, onAnswer, type, explanation }) {
  const [inputValue, setInputValue] = useState('');
  const [showExplanation, setShowExplanation] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleOptionClick = (option) => {
    if (selectedOption) return; // Prevent multiple selections
    setSelectedOption(option);
    onAnswer(option);
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleNext = () => {
    if (inputValue === '') {
      alert('Please enter a value.');
      return;
    }
    onAnswer(inputValue);
    setInputValue('');
  };

  const toggleExplanation = () => {
    setShowExplanation((prev) => !prev);
  };

  return (
    <div className="question-container">
      <div className="question-card">
        <h2 className="question-text">{question}</h2>

        {explanation && (
          <button
            className="explanation-toggle"
            onClick={toggleExplanation}
            aria-expanded={showExplanation}
            aria-controls="explanation-content"
          >
            {showExplanation ? 'Hide Explanation' : 'Need Help?'}
          </button>
        )}

        {showExplanation && explanation && (
          <div
            id="explanation-content"
            className="question-explanation"
            dangerouslySetInnerHTML={{ __html: marked.parse(explanation) }}
          />
        )}

        {type === 'options' && (
          <div className="options-container">
            {options.map((option) => (
              <button
                key={option}
                className={`option-button ${
                  selectedOption === option ? 'selected' : ''
                }`}
                onClick={() => handleOptionClick(option)}
                disabled={!!selectedOption}
                aria-pressed={selectedOption === option}
              >
                {option}
              </button>
            ))}
          </div>
        )}

        {type === 'number' && (
          <div className="input-container">
            <input
              type="number"
              className="number-input"
              value={inputValue}
              onChange={handleInputChange}
              min="1"
              aria-label="Enter a number"
            />
            <button
              className="next-button"
              onClick={handleNext}
              disabled={isSubmitting}
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

// Define PropTypes for better type checking
Question.propTypes = {
  question: PropTypes.string.isRequired,
  options: PropTypes.arrayOf(PropTypes.string),
  onAnswer: PropTypes.func.isRequired,
  type: PropTypes.oneOf(['options', 'number', 'text']).isRequired,
  explanation: PropTypes.string,
};

// Define default props
Question.defaultProps = {
  options: [],
  explanation: '',
};

export default Question;
