// src/Assessment.js
import React, { useState } from 'react';
import Question from './Question';
import Outcome from './Outcome';

function Assessment() {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const handleAnswer = (questionKey, value) => {
    setAnswers((prevAnswers) => ({ ...prevAnswers, [questionKey]: value }));

    // Conditional navigation logic
    if (questionKey === 'residency' && value === 'No') {
      setStep(questions.length + 1); // Skip to outcome
      return;
    }

    if (questionKey === 'age' && value === 'Under 18') {
      setStep(questions.length + 1); // Skip to outcome
      return;
    }

    if (questionKey === 'medicareEnrolled' && value === 'No') {
      // Skip Question 5 (dualEligible)
      setStep((prevStep) => prevStep + 2);
      return;
    }

    if (questionKey === 'dualEligible' && value === 'No') {
      // Skip to Question 6
      setStep((prevStep) => prevStep + 1);
      return;
    }

    if (questionKey === 'disability' && value === 'No') {
      // Skip to Question 7
      setStep((prevStep) => prevStep + 1);
      return;
    }

    if (questionKey === 'esrd' && value === 'Yes') {
      // Skip to Question 8
      setStep((prevStep) => prevStep + 1);
      return;
    }

    if (questionKey === 'abiOrTbi' && value === 'No') {
      // Skip to Question 11
      setStep(11);
      return;
    }

    if (questionKey === 'nursingFacility' && value === 'No') {
      // Skip to Question 10
      setStep(10);
      return;
    }

    if (questionKey === 'intellectualDisability' && value === 'No') {
      // Skip to Question 12
      setStep(12);
      return;
    }

    setStep((prevStep) => prevStep + 1);
  };

  const questions = [
    // Question 1
    {
      key: 'residency',
      question: 'Do you currently reside in Massachusetts?',
      options: ['Yes', 'No'],
    },
    // Question 2
    {
      key: 'age',
      question: 'What is your age?',
      options: ['Under 18', '18-21', '21-64', '65 or older'],
    },
    // Question 3
    {
      key: 'incomeEligible',
      question:
        'Do you meet the income and asset limits for MassHealth Standard based on your household size?',
      options: ['Yes', 'No'],
    },
    // Question 4
    {
      key: 'medicareEnrolled',
      question: 'Are you enrolled in Medicare?',
      options: ['Yes', 'No'],
    },
    // Question 5
    {
      key: 'dualEligible',
      question:
        'Are you eligible for both MassHealth Standard and Medicare (dual-eligible)?',
      options: ['Yes', 'No'],
    },
    // Question 6
    {
      key: 'disability',
      question: 'Do you have a disability?',
      options: ['Yes', 'No'],
    },
    // Question 7
    {
      key: 'esrd',
      question: 'Do you have end-stage renal disease (ESRD)?',
      options: ['Yes', 'No'],
    },
    // Question 8
    {
      key: 'abiOrTbi',
      question:
        'Do you have an acquired brain injury (ABI) or traumatic brain injury (TBI)?',
      options: ['Yes', 'No'],
    },
    // Question 9
    {
      key: 'nursingFacility',
      question:
        'Are you currently residing in a nursing facility or hospital, or have you been in one for at least 90 consecutive days?',
      options: ['Yes', 'No'],
    },
    // Question 10
    {
      key: 'nursingCareCriteria',
      question:
        'Do you meet the criteria for needing nursing facility level of care?',
      options: ['Yes', 'No'],
    },
    // Question 11
    {
      key: 'intellectualDisability',
      question:
        'Do you have an intellectual disability diagnosed by the Department of Developmental Services (DDS)?',
      options: ['Yes', 'No'],
    },
    // Question 12
    {
      key: 'communitySupportNeeded',
      question:
        'Do you require assistance with daily activities to live in the community (e.g., personal care, homemaker services)?',
      options: ['Yes', 'No'],
    },
    // Question 13
    {
      key: 'transitioningFromInstitutionalCare',
      question:
        'Are you planning to transition from a nursing facility or hospital back into the community?',
      options: ['Yes', 'No'],
    },
    // Question 14
    {
      key: 'coordinatedCareNeeded',
      question:
        'Would you benefit from coordinated health care services, including medical, behavioral health, and long-term services and supports?',
      options: ['Yes', 'No'],
    },
  ];

  const determineEligibility = () => {
    const {
      residency,
      age,
      incomeEligible,
      medicareEnrolled,
      dualEligible,
      esrd,
      disability,
      abiOrTbi,
      nursingFacility,
      nursingCareCriteria,
      intellectualDisability,
      communitySupportNeeded,
      transitioningFromInstitutionalCare,
      coordinatedCareNeeded,
    } = answers;

    let eligiblePrograms = [];

    // Residency check
    if (residency !== 'Yes') {
      return ['Ineligible for all listed MassHealth programs and waivers.'];
    }

    // Income eligibility check
    if (incomeEligible === 'No') {
      return ['May not be eligible for MassHealth Standard or associated waivers.'];
    }

    // MassHealth Standard
    eligiblePrograms.push('MassHealth Standard');

    // Age-specific programs
    if (age === 'Under 18') {
      eligiblePrograms.push("Children's MassHealth Standard");
    }

    // Dual-eligible programs
    if (medicareEnrolled === 'Yes' && dualEligible === 'Yes') {
      if (age === '21-64' && esrd === 'No') {
        eligiblePrograms.push('Integrated Care Options (ICO)');
        eligiblePrograms.push('One Care Program');
      }
      if (age === '65 or older') {
        eligiblePrograms.push('Senior Care Options (SCO)');
      }
    }

    // Disability programs
    if (disability === 'Yes') {
      // May qualify for additional supports under MassHealth Standard
    }

    // ESRD exclusion
    if (esrd === 'Yes') {
      // Not eligible for ICO and One Care Program
      eligiblePrograms = eligiblePrograms.filter(
        (program) => program !== 'Integrated Care Options (ICO)' && program !== 'One Care Program'
      );
    }

    // ABI/TBI Waivers
    if (abiOrTbi === 'Yes') {
      if (nursingFacility === 'Yes') {
        eligiblePrograms.push('ABI Residential Habilitation (ABI-RH)');
        eligiblePrograms.push('ABI Non-Residential Habilitation (ABI-N)');
        eligiblePrograms.push('MFP Residential Supports (MFP-RS)');
        eligiblePrograms.push('MFP Community Living (MFP-CL)');
        eligiblePrograms.push('Traumatic Brain Injury (TBI) Waiver');
      } else if (nursingCareCriteria === 'Yes') {
        eligiblePrograms.push('Traumatic Brain Injury (TBI) Waiver');
      }
    }

    // Frail Elder Waiver
    if (age === '60+' && nursingCareCriteria === 'Yes') {
      eligiblePrograms.push('Frail Elder Waiver (FEW)');
    }

    // Intellectual Disability
    if (intellectualDisability === 'Yes' && (age === '22 or older' || age === '65 or older')) {
      eligiblePrograms.push('Adult Supports Waiver (DDS-AS)');
    }

    // Need for community support
    if (communitySupportNeeded === 'Yes') {
      eligiblePrograms.push('Frail Elder Waiver (FEW)');
      if (transitioningFromInstitutionalCare === 'Yes') {
        eligiblePrograms.push('MFP Community Living (MFP-CL)');
      }
    }

    // Transitioning from Institutional Care
    if (transitioningFromInstitutionalCare === 'Yes') {
      eligiblePrograms.push('MFP Residential Supports (MFP-RS)');
      eligiblePrograms.push('MFP Community Living (MFP-CL)');
    }

    // Coordinated Care Needs
    if (coordinatedCareNeeded === 'Yes') {
      if (age === '21-64' && dualEligible === 'Yes' && esrd === 'No') {
        eligiblePrograms.push('Integrated Care Options (ICO)');
        eligiblePrograms.push('One Care Program');
      }
      if ((age === '65 or older' || age === '65+') && dualEligible === 'Yes') {
        eligiblePrograms.push('Senior Care Options (SCO)');
      }
    }

    // Remove duplicates
    eligiblePrograms = [...new Set(eligiblePrograms)];

    if (eligiblePrograms.length === 0) {
      eligiblePrograms.push(
        'Based on your answers, you may need to contact MassHealth for more information.'
      );
    }

    return eligiblePrograms;
  };

  if (step > questions.length) {
    const eligiblePrograms = determineEligibility();
    return <Outcome programs={eligiblePrograms} />;
  }

  const currentQuestion = questions[step - 1];

  return (
    <Question
      question={currentQuestion.question}
      options={currentQuestion.options}
      onAnswer={(value) => handleAnswer(currentQuestion.key, value)}
    />
  );
}

export default Assessment;
