// src/Question.js
import React, { useState } from 'react';
import { marked } from 'marked';

function Question({ question, options, onAnswer, type, explanation }) {
  const [inputValue, setInputValue] = useState('');
  const [showExplanation, setShowExplanation] = useState(false);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleNext = () => {
    if (inputValue === '') {
      alert('Please enter a value.');
      return;
    }
    onAnswer(inputValue);
    setInputValue('');
  };

  const toggleExplanation = () => {
    setShowExplanation((prev) => !prev);
  };

  return (
    <div className="question-container">
      <div className="question-card">
        <h2 className="question-text">{question}</h2>
        {explanation && (
          <button className="explanation-toggle" onClick={toggleExplanation}>
            {showExplanation ? 'Hide Explanation' : 'Need Help?'}
          </button>
        )}
        {showExplanation && explanation && (
          <div
            className="question-explanation"
            dangerouslySetInnerHTML={{ __html: marked.parse(explanation) }}
          />
        )}
        {type === 'options' && (
          <div className="options-container">
            {options.map((option) => (
              <button
                key={option}
                className="option-button"
                onClick={() => onAnswer(option)}
              >
                {option}
              </button>
            ))}
          </div>
        )}
        {type === 'number' && (
          <div className="input-container">
            <input
              type="number"
              className="number-input"
              value={inputValue}
              onChange={handleInputChange}
              min="1"
            />
            <button className="next-button" onClick={handleNext}>
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Question;
